import React, { Component } from 'react';
import s from './Languages.css';
import enFlag from './images/english.png';
import krFlag from './images/korean.png';
import deFlag from './images/german.png';
import esFlag from './images/spanish.png';

var LANGUAGES = [
  {name: 'en', imgSrc: enFlag},
  {name: 'ko', imgSrc: krFlag},
  {name: 'de', imgSrc: deFlag},
  {name: 'es', imgSrc: esFlag},
];

export default class Languages extends Component {

  render() {
    const languagesItems = LANGUAGES.map((lang) =>
      <a onClick={this.props.onChange} key={lang.name} >
        <img src={lang.imgSrc} alt={lang.name} />
      </a>
    );

    return (
      <span className={ s.languages }>
        { languagesItems }
      </span>
    );
  }
}